import React, { Component } from 'react';

class SignUp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: "",
            password: "",
            pswconf: "",
            name: "",
            surname: "",
            flash: ""
        }
        this.updateEmailField = this.updateEmailField.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    updateEmailField = (e) => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    }

    handleSubmit = (e) => {
        fetch("/auth/signup",
            {
                method: 'POST',
                headers: new Headers({
                    'Content-Type': 'application/json'
                }),
                body: JSON.stringify(this.state),
            })
            .then(res => res.json())
            .then(
                res => this.setState({ "flash": res.flash }),
                err => this.setState({ "flash": err.flash })              
            );                 
        e.preventDefault();

    }

    render() {
        return (
            <div>
                <div> Sign Up </div>
                <h1>Email: {this.state.email}</h1>
                <form onSubmit={this.handleSubmit}>
                    <div>
                        <label>Email</label>
                        <input type="email" name="email" value={this.state.email} onChange={this.updateEmailField}></input>
                    </div>
                    <div>
                        <label>password</label>
                        <input type="text" name="password" value={this.state.password} onChange={this.updateEmailField}></input>
                    </div>
                    <div>
                        <label>password confirmation</label>
                        <input type="text" name="pswconf" value={this.state.pswconf} onChange={this.updateEmailField}></input>
                    </div>
                    <div>
                        <label>Name</label>
                        <input type="text" name="name" value={this.state.name} onChange={this.updateEmailField} ></input>
                    </div>
                    <div>
                        <label>Surname</label>
                        <input type="text" name="surname" value={this.state.surname} onChange={this.updateEmailField} ></input>
                    </div>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        );
    }
}

export default SignUp;
